      errCode: 1,
      errMessage: "Missing parameter requied",
    });
  }
  let response = await handleDeleteProductCartUser(idUser, keyProduct);
  return res.status(200).json({
    errCode: response.errCode,
    errMessage: response.errMessage,
  });
};
